object SentenceSplitter extends (String => Seq[String]) {
  override def apply(v1: String): Seq[String] =
    v1.split("[.!?]").toList
}
object TokenSplitter extends (Seq[String] => Seq[Seq[String]]) {
  override def apply(v1: Seq[String]): Seq[Seq[String]] =
    v1.map(s => s.split(" ").toList)
}
val pipeline = SentenceSplitter andThen TokenSplitter
pipeline("Of course, the whole point of a Doomsday Machine " +
         "is lost, if you *keep* it a *secret*! Why didn't " +
         "you tell the world, EH?")

