def step1stemmer(word: String): String = {
 word match {
   case i if (i.takeRight(1)=="s") => i.dropRight(1)
   case i if (i.takeRight(3)=="ing") => i.dropRight(3)
   case i if (i.takeRight(2)=="ed") => i.dropRight(2)
   case _ => word
 }
}
step1stemmer("learning")

