def parseArgument(arg: String, value: Any): String =
  (arg, value) match {
 case ("-x", x) => "-x" + x
 case ("-v", v: Int) if (v<3) => "-v < 3"
 case ("-v", weird_value) => "-v weird value!!"
 case unk => "An unknown argument: " + unk
}
parseArgument("-v", 0.5)

